-- -----------------------------------------------------
-- Schema manga-oshop
-- -----------------------------------------------------

-- -----------------------------------------------------
-- Table `editor`
-- -----------------------------------------------------
DROP TABLE IF EXISTS `editor` ;

CREATE TABLE IF NOT EXISTS `editor` (
  `id` INT UNSIGNED NOT NULL AUTO_INCREMENT,
  `name` VARCHAR(45) NOT NULL COMMENT "Le nom de l'éditeur",
  `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT "La date de création de l'éditeur",
  `updated_at` TIMESTAMP NULL COMMENT "La date de la dernière mise à jour de l'éditeur",
  PRIMARY KEY (`id`));

-- -----------------------------------------------------
-- Table `price`
-- -----------------------------------------------------
DROP TABLE IF EXISTS `price` ;

CREATE TABLE IF NOT EXISTS `price` (
  `id` INT UNSIGNED NOT NULL AUTO_INCREMENT,
  `amount` DECIMAL(10,2) NOT NULL DEFAULT 0 COMMENT 'Le prix du produit (du manga)',  `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT "La date de création du prix du produit (du manga)",
  `updated_at` TIMESTAMP NULL COMMENT "La date de la dernière mise à jour du prix du produit (du manga)",
  PRIMARY KEY (`id`));

-- -----------------------------------------------------
-- Table `category`
-- -----------------------------------------------------
DROP TABLE IF EXISTS `category` ;

CREATE TABLE IF NOT EXISTS `category` (
  `id` INT UNSIGNED NOT NULL AUTO_INCREMENT,
  `name` VARCHAR(64) NOT NULL COMMENT 'Le nom de la catégorie du manga',
  `subtitle` VARCHAR(256) NULL,
  `picture` VARCHAR(128) NULL COMMENT "L'URL de l'image de la catégorie du manga",
  `url` VARCHAR(45) NULL COMMENT "Le nom de l'url de de la catégorie (du manga)",
  `home_order` TINYINT(1) NOT NULL DEFAULT 0 COMMENT "L'ordre d'affichage de la catégorie sur la home (0=pas affichée en home)",
  `home_subtitle` VARCHAR(64) NULL,
  `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'La date de création de la catégorie',
  `updated_at` TIMESTAMP NULL COMMENT 'La date de la dernière mise à jour de la catégorie',
  PRIMARY KEY (`id`));

-- -----------------------------------------------------
-- Table `product`
-- -----------------------------------------------------
DROP TABLE IF EXISTS `product` ;

CREATE TABLE IF NOT EXISTS `product` (
  `id` INT UNSIGNED NOT NULL AUTO_INCREMENT,
  `name` VARCHAR(45) NOT NULL COMMENT 'Le nom du produit (du manga)',
  `author` VARCHAR(45) NOT NULL COMMENT "Le nom de l'auteur du produit (du manga)",
  `description` TEXT NULL COMMENT 'La description du produit (du manga)',
  `picture` VARCHAR(128) NULL COMMENT "L'URL de l'image du produit (du manga)",
  `url` VARCHAR(45) NOT NULL COMMENT "Le nom de l'url du produit (du manga)",
  `status` TINYINT(1) NOT NULL DEFAULT 0 COMMENT 'Le statut du produit (0=non renseignée, 1=dispo, 2=pas dispo) (du manga)',
  `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'La date de création du produit (du manga)',
  `updated_at` TIMESTAMP NULL COMMENT 'La date de la dernière mise à jour du produit (du manga)',
  `tome_id` INT UNSIGNED NOT NULL,
  `category_id` INT UNSIGNED NOT NULL,
  `editor_id` INT UNSIGNED NOT NULL,
  `price_id` INT UNSIGNED NULL,
  PRIMARY KEY (`id`));

ALTER TABLE product ADD CONSTRAINT fk_product_category_id FOREIGN KEY (category_id) REFERENCES category(id) ;
ALTER TABLE product ADD CONSTRAINT fk_product_editor_id FOREIGN KEY (editor_id) REFERENCES editor(id) ;
ALTER TABLE product ADD CONSTRAINT fk_product_price_id FOREIGN KEY (price_id) REFERENCES price(id) ;

