INSERT INTO `product` (`id`, `name`, `author`,`description`, `picture`, `url`, `status`, `created_at`, `updated_at`, `tome_id`, `category_id`, `editor_id`, `price_id`) VALUES (93, "Ton visage au Clair de Lune", "Yamamori Mika", "Yoi a des traits fins, un corps svelte et une voix au timbre bas... Résultat : tout le monde considère Yoi comme un très beau garçon, quitte à en oublier qu'elle est une fille. Adulée de tous, surtout de la gent féminine, de par son allure de héros de conte de fées, elle est surnommée 'le prince' par ses camarades de classe. La lycéenne éprouve des sentiments partagés face à cette étrange forme de popularité. Sa rencontre avec Ichimura, l'autre 'prince' du lycée qui, lui, est bel et bien un garçon, pourrait bousculer l'image que Yoi a d'elle-même...", 'assets/images/products/ton-visage-au-clair-de-lune-tome-1.jpg', 'ton-visage-au-clair-de-lune', 1, '2024-01-18 20:00:00', NULL, 1, 3, 7, 3);
